sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var incID = 'e8eee670db0b00d02cc15c8a689619bb';//current.sys_id;
	var snapshotMethods = new SLSIncidentSnapshotUtils(incID);

	var totalComplaints = snapshotMethods.getTotalComplaint();
 	var firstComplaint = snapshotMethods.getFirstComplaint();
 	var lastComplaint = snapshotMethods.getLastComplaint();
 	var incData = snapshotMethods.getPRStatus();
 	var appVersion = snapshotMethods.getAppVersion();
	var platform = snapshotMethods.getPlatform();
	var location = snapshotMethods.getlocation();
	var avg = snapshotMethods.getAVG();
	var originalLTV = snapshotMethods.getOriginalLTV();


	var snapshot = "<p><i><b>Complaints in total: </b></i>" + totalComplaints +
		"<br><i><b> Avg per day: </b></i>" + avg +
		"<br><i><b> First complaint: </b></i>" + firstComplaint +
		"<br><i><b> Last complaint: </b></i>" + lastComplaint +
		"<br><i><b> PR Status: </b></i>" + incData + 
		"<br><i><b> Original LTV: </b></i>" + originalLTV +
		"<br><i><b> App Version: </b></i>" + appVersion + 
		"<br><i><b> Platform: </b></i>" + platform +
		"<br><i><b> Location: </b></i>" + location +
		"</p>";

	current.sls_incident_snapshot = snapshot;
	current.update();



	})(current, previous);
