sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {

	var output = '';
	var from;

	var grInc = new GlideRecord('x_play4_sls_incident');
	grInc.addQuery('sys_id', current.sysapproval);
	grInc.query();
	if (grInc.next()) {

		output = grInc.short_description.getDisplayValue() + " is awaiting for your approval " + '<br /><br />' +
		'<b>Number: </b>' + grInc.getValue('number') +  '<br />' +
		'<b>Opened by: </b>' + grInc.opened_by.getDisplayValue() + '<br />' +
		'<b>Affected Business Service: </b>' + grInc.sls_business_service.getDisplayValue() + '<br />' +
		'<b>Priority: </b>' + grInc.sls_priority.getDisplayValue() + '<br />' + 
		'<b>Category: </b>' + grInc.category.getDisplayValue() +  '<br />' + 
		'<b>Subcategory: </b>' + grInc.subcategory.getDisplayValue() + '<br />' +
		'<div><span style="font-size: 12pt;"><strong>Description: </strong>&nbsp;' + grInc.description.getDisplayValue() + '</span></div>';

		from = grInc.sls_assignment_group.email.getDisplayValue();

	}

	email.setFrom(from);
	template.print(output);

})(current, template, email, email_action, event);
