sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
    var status;
	var result;
    status = event.parm1 || current.status.getDisplayValue();
	result = '<div><span style="font-size: 12pt;">The incident&nbsp;' + current.number + ' was <b>' + status +  '</b></span></div>'
				 +'<div><span style="font-size: 12pt;">Opened by: ' + current.opened_by.getDisplayValue() + '</span></div>';
    template.print(result);
})(current, template, email, email_action, event);
