sys_script_include.script:
var addTimeSpent = Class.create();
addTimeSpent.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
slsAddTimeSpent:function(){
	var incSysId = this.getParameter('sysparm_inc_id'); 
	var duration = this.getParameter('sysparm_duration'); 
	var affectedBS = this.getParameter('sysparm_affbc'); 
	var isOnDuty = this.getParameter('sysparm_onduty');
	var gr = new GlideRecord ('x_play4_sls_incident');
	if(gr.get(incSysId)){
		if(duration){
			var timems = gr.u_time_spent.dateNumericValue();
			timems = timems + duration*60*1000; 
			gr.u_time_spent.setDateNumericValue(timems);
			gr.update();
		}
	}

	var timeSpentGR = new GlideRecord('x_play4_sls_time_spent');
	timeSpentGR.initialize();
	timeSpentGR.u_duration.setDateNumericValue(duration*60*1000);
	timeSpentGR.setValue('u_creator', gs.getUserID());
	timeSpentGR.setValue('u_incident_number', incSysId);
	timeSpentGR.setValue('u_affected_business_service', affectedBS);
	timeSpentGR.setValue('u_on_duty', isOnDuty);
	timeSpentGR.insert();
},

getTimeSpentValue: function(){
	var incSysId = this.getParameter('sysparm_inc_id'); 
	var gr = new GlideRecord ('x_play4_sls_incident');
	if(gr.get(incSysId)){
		return gr.getValue("u_time_spent");
	}
},
    type: 'addTimeSpent'
});
