sys_ui_page.client_script:
function copyEmailAddress(){
	copyToClipboard(document.getElementById('emailAddress').value);
}

function copyWatermark(){
	copyToClipboard(document.getElementById('watermark').value);
}

(function (){
		var watermarkGA = new GlideAjax('SLS_watermark');
		watermarkGA.addParam('sysparm_name', 'getSysProp');
		watermarkGA.addParam('propName', 'glide.email.operations');
		watermarkGA.getXML(propResp);

		function propResp(response){
			 var answer = response.responseXML.documentElement.getAttribute("answer");
			 document.getElementById('emailAddress').value = answer;
		}
}());

(function (){
	var watermarkGA = new GlideAjax('SLS_watermark');
	watermarkGA.addParam('sysparm_name', 'getCurrentWatermark');
	watermarkGA.addParam('currentSysId', "$[jvar_pageId]");
	watermarkGA.getXML(responseHandling);

	function responseHandling(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer != 'false'){
			document.getElementById('watermark').value = answer;
		}
	}
}());
