answer = usrHasRole(current.opened_by.toString());

function usrHasRole(userID) {  
		var hasRoleGR = new GlideRecord('sys_user_has_role');
		  hasRoleGR.addQuery('user',userID);
		  hasRoleGR.addQuery('role.name','Customer support');
		  hasRoleGR.query();
		  if(hasRoleGR.next()){
			return 'yes';  
		  }
		return 'no';
}

